import React from 'react';
import { StyledItem, StyledItemsGrid } from '../styles/Grid';

const LoadingGrid = ({ count }) => (
  <StyledItemsGrid>
    {Array.from({ length: count }, (_, i) => (
      <StyledItem key={i}>
        <p className="mark">Loading...</p>
        <img
          className="loading"
          alt="loading"
          src="data:image/png;base64,"
          width={500}
          height={400}
        />
      </StyledItem>
    ))}
  </StyledItemsGrid>
);

export default LoadingGrid;
